int pmod_mat_inv(pmod_mat_t *B, pmod_mat_t *A, int A_r, int A_c, int (*pmod_mat_syst_fun)(pmod_mat_t *))
{
  PROFILER_START("pmod_mat_inv");
  pmod_mat_t M[A_r * A_c * 2];

  for (int r = 0; r < A_r; r++)
  {
    memcpy(&M[r * A_c * 2], &A[r * A_c], A_c * sizeof(GFq_t));

    for (int c = 0; c < A_c; c++)
      pmod_mat_set_entry(M, A_r, A_c * 2, r, A_c + c, r == c ? 1 : 0);
  }

  PROFILER_START("pmod_mat_syst");
  int ret = pmod_mat_syst_fun(M);
  PROFILER_STOP("pmod_mat_syst");

  if ((ret == 0) && B)
    for (int r = 0; r < A_r; r++)
      memcpy(&B[r * A_c], &M[r * A_c * 2 + A_c], A_c * sizeof(GFq_t));

  PROFILER_STOP("pmod_mat_inv");
  return ret;
}

int pmod_mat_inv_nct(pmod_mat_t *B, pmod_mat_t *A, int A_r, int A_c, int (*pmod_mat_syst_fun)(pmod_mat_t *, uint16_t *inverse_table))
{
  PROFILER_START("pmod_mat_inv");
  pmod_mat_t M[A_r * A_c * 2];

  for (int r = 0; r < A_r; r++)
  {
    memcpy(&M[r * A_c * 2], &A[r * A_c], A_c * sizeof(GFq_t));

    for (int c = 0; c < A_c; c++)
      pmod_mat_set_entry(M, A_r, A_c * 2, r, A_c + c, r == c ? 1 : 0);
  }

  PROFILER_START("pmod_mat_syst");
  int ret = pmod_mat_syst_fun(M, mod_inverse_table);
  PROFILER_STOP("pmod_mat_syst");

  if ((ret == 0) && B)
    for (int r = 0; r < A_r; r++)
      memcpy(&B[r * A_c], &M[r * A_c * 2 + A_c], A_c * sizeof(GFq_t));

  PROFILER_STOP("pmod_mat_inv");
  return ret;
}
